/**
 * Login screen for user authentication
 */
@Composable
fun LoginScreen(
 onLoginSuccess: () -> Unit = {},
 viewModel: LoginViewModel = hiltViewModel(),
) {
 val uiState by viewModel.uiState.collectAsState()
 val layoutDirection = LocalLayoutDirection.current
 val isRtl = layoutDirection == LayoutDirection.Rtl

 // Handle successful login navigation
 LaunchedEffect(uiState.isLoginSuccessful) {
 if (uiState.isLoginSuccessful) {
 onLoginSuccess()
 }
 }

 Box(
 modifier = Modifier
 .fillMaxSize()
 .background(BackgroundGray)
 ) {
 // Logo
 Image(
 painter = painterResource(id = R.drawable.onxrestaurant_logo),
 contentDescription = "Onyx Logo",
 modifier = Modifier
 .align(Alignment.TopStart)
 .padding(top = 36.dp, start = 26.dp)
 .width(170.dp)
 .height(75.dp),
 contentScale = ContentScale.FillBounds
 )

 // Top right quarter circle
 Image(
 painter = painterResource(id = R.drawable.log_in_quarter_circle),
 contentDescription = null,
 modifier = Modifier
 .align(Alignment.TopEnd)
 .graphicsLayer {
 scaleX = if (isRtl) -1f else 1f
 },
 contentScale = ContentScale.FillBounds,
 )

 // Language selection button
 LanguageSelection(
 viewModel = viewModel,
 isRtl = isRtl,
 modifier = Modifier
 .align(Alignment.TopEnd)
 .padding(18.dp)
 )

 // Main content column
 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(16.dp),
 horizontalAlignment = Alignment.CenterHorizontally,
 verticalArrangement = Arrangement.Bottom
 ) {
 // Welcome text
 Text(
 text = stringResource(R.string.welcome_back),
 fontSize = 29.sp,
 fontWeight = FontWeight.Bold,
 color = PrimaryTeal,
 modifier = Modifier.padding(top = 40.dp)
 )

 Text(
 text = stringResource(R.string.login_subtitle),
 fontSize = 12.sp,
 color = PrimaryTeal,
 modifier = Modifier.padding(top = 8.dp, bottom = 24.dp)
 )

 // User ID field
 LoginTextField(
 value = uiState.userId,
 onValueChange = viewModel::onUserIdChange,
 label = stringResource(R.string.user_id),
 modifier = Modifier.padding(top = 16.dp)
 )

 // Password field
 LoginTextField(
 value = uiState.password,
 onValueChange = viewModel::onPasswordChange,
 label = stringResource(R.string.password),
 visualTransformation = if (uiState.isPasswordVisible) VisualTransformation.None
 else PasswordVisualTransformation(),
 modifier = Modifier.padding(top = 16.dp)
 )

 // Show/Hide password toggle
 Text(
 text = stringResource(R.string.show_more),
 color = PrimaryTeal,
 fontSize = 14.sp,
 fontWeight = FontWeight.Medium,
 modifier = Modifier
 .padding(16.dp)
 .align(Alignment.End)
 .graphicsLayer(alpha = 0.99f) // Improves render performance
 )

 // Login button
 LoginButton(
 text = stringResource(R.string.login),
 onClick = viewModel::login,
 isLoading = uiState.isLoading,
 modifier = Modifier
 .fillMaxWidth()
 )

 Spacer(modifier = Modifier.height(36.dp))

 // Delivery illustration
 Image(
 painter = painterResource(id = R.drawable.log_in_delivery_car),
 contentDescription = "Delivery illustration",
 modifier = Modifier
 .height(170.dp)
 .width(195.dp)
 .padding(bottom = 16.dp)
 .graphicsLayer {
 scaleX = if (isRtl) -1f else 1f
 },
 contentScale = ContentScale.Fit
 )
 }

 // Language Dialog
 if (uiState.isLanguageDialogVisible) {
 LanguageDialog(
 languages = uiState.availableLanguages,
 onLanguageSelected = viewModel::selectAndApplyLanguage,
 onDismiss = viewModel::toggleLanguageDialog
 )
 }

 // Error toast
 if (uiState.errorMessage != null) {
 AppToast(
 message = uiState.errorMessage!!,
 type = ToastType.ERROR,
 title = uiState.errorMessageTitle,
 onDismiss = viewModel::clearError
 )
 }
 }
}
