package com.androidghanem.domain.model

enum class OrderStatus {
 NEW,
 DELIVERING, 
 DELIVERED,
 PARTIAL_RETURN,
 RETURNED;

 fun getDisplayText(isArabic: Boolean = false): String {
 return when (this) {
 NEW -> if (isArabic) "" else "New"
 DELIVERING -> if (isArabic) " " else "Delivering"
 DELIVERED -> if (isArabic) " " else "Delivered"
 PARTIAL_RETURN -> if (isArabic) " " else "Partial Return"
 RETURNED -> if (isArabic) " " else "Returned"
 }
 }
}

