 val deliveryName = response.Data?.DeliveryName ?: ""
 Result.success(DeliveryDriverInfo(deliveryId = deliveryId, name = deliveryName))
 } else {
 Result.failure(Exception(response.Result.ErrMsg))
 }
 } catch (e: Exception) {
 Result.failure(e)
 }
 }

