 P_DLVRY_NO = deliveryId,
 P_OLD_PSSWRD = oldPassword,
 P_NEW_PSSWRD = newPassword
 )
 )

 val response = apiService.changeDeliveryPassword(request)

 if (response.Result.ErrNo == 0) {
 Result.success(true)
 } else {
 Result.failure(Exception(response.Result.ErrMsg))
 }
 } catch (e: Exception) {
 Result.failure(e)
 }
 }

